//1.导入 useNavigate 钩子函数
import {useNavigate} from 'react-router-dom'


function Login(){

  //2.执行钩子函数得到【跳转函数】
  const navigate = useNavigate()

  //3.定义跳转函数，传入目标【路由路径】
  function goAbout (){
    navigate('/about')
  }


  return(
    <div>
      Login
      <button onClick={goAbout}> 跳转到关于 </button>
    </div>
  )
}

export default Login
